select bol.log_id, bol.log_date, bo.business_opportunity_name, bo.business_opportunity_type, bol.create_by, bol.event_type, bol.specific_event, bol.working_hours, bol.internal_participant, bol.external_participant, sbo.user_id from business_opportunity_log bol left join business_opportunity bo on bol.business_opportunity_id = bo.business_opportunity_id left join sale_business_opportunity sbo on bol.business_opportunity_id = sbo.business_opportunity_id LEFT JOIN user_role ur on bol.create_by = ur.user_id where (ur.role_id = 3 or ur.role_id = 2)
and bo.business_opportunity_name like #{businessOpportunityName,jdbcType=VARCHAR}
and DATE_FORMAT(bol.log_date,'%Y-%m-%d') >= #{startTime,jdbcType=VARCHAR}
and #{endTime,jdbcType=VARCHAR} >= DATE_FORMAT(bol.log_date,'%Y-%m-%d')
and bol.event_type = #{eventType,jdbcType=VARCHAR}
and (bol.create_by = #{userId,jdbcType=BIGINT} or sbo.user_id = #{userId,jdbcType=BIGINT})
and bol.create_by in
#{item}
