select DATE_FORMAT(bo.create_time,'%Y-%m-%d') create_time, bo.business_opportunity_id, bo.business_opportunity_name, bo.business_opportunity_num, bo.business_opportunity_type, bo.business_opportunity_process, bo.create_by, bo.is_closed, bo.is_cancellation, sbo.user_id from business_opportunity bo left join sale_business_opportunity sbo on bo.business_opportunity_id = sbo.business_opportunity_id where bo.is_cancellation = 0
and bo.business_opportunity_name like #{businessOpportunityName,jdbcType=VARCHAR}
and DATE_FORMAT(bo.create_time,'%Y-%m-%d') >= #{startTime,jdbcType=VARCHAR}
and #{endTime,jdbcType=VARCHAR} >= DATE_FORMAT(bo.create_time,'%Y-%m-%d')
and bo.business_opportunity_process = #{businessOpportunityProcess,jdbcType=VARCHAR}
and sbo.user_id in
#{item}
order by bo.create_time desc limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

