select
count(1)
from  business_opportunity
where 
 address_province = #{province,jdbcType=VARCHAR}
 and address_city = #{city,jdbcType=VARCHAR}
 and business_opportunity_type = #{businessOpportunityType,jdbcType=INTEGER}

select
count(1)
from  business_opportunity
where 
 address_province = #{province,jdbcType=VARCHAR}
 and address_city = #{city,jdbcType=VARCHAR}
 and business_opportunity_type = #{businessOpportunityType,jdbcType=INTEGER}
 and business_opportunity_name = #{businessOpportunityName,jdbcType=VARCHAR}

select bo.* from business_opportunity bo , sale_business_opportunity sbo where bo.business_opportunity_id = sbo.business_opportunity_id and bo.is_cancellation = 0 and bo.is_closed = 0 and bo.business_opportunity_type = 1
and sbo.user_id = #{userId,jdbcType=BIGINT}
and bo.business_opportunity_name like #{businessOpportunityName,jdbcType=VARCHAR}

select count(1) from cooperative_partner_school s, business_opportunity b, sale_business_opportunity sbo where s.business_opportunity_id = b.business_opportunity_id and b.business_opportunity_id = sbo.business_opportunity_id and b.is_cancellation = 0 and b.is_closed = 0 and b.business_opportunity_type = 1
and b.business_opportunity_name LIKE #{businessOpportunityName,jdbcType=VARCHAR}
and s.school_name LIKE #{schoolName,jdbcType=VARCHAR}
and sbo.user_id = #{userId,jdbcType=BIGINT}

select b.business_opportunity_name, s.school_name, s.address_province, s.address_city, s.address_country, s.address_detail from cooperative_partner_school s, business_opportunity b, sale_business_opportunity sbo where s.business_opportunity_id = b.business_opportunity_id and b.business_opportunity_id = sbo.business_opportunity_id and b.is_cancellation = 0 and b.is_closed = 0 and b.business_opportunity_type = 1
and b.business_opportunity_name LIKE #{businessOpportunityName,jdbcType=VARCHAR}
and s.school_name LIKE #{schoolName,jdbcType=VARCHAR}
and sbo.user_id = #{userId,jdbcType=BIGINT}
order by b.create_time desc limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
