mapper.insert:

insert into user (user_id, login_name, password, 
  name, gender, phone, 
  email, employee_num, is_employment, 
  entry_date, create_time, update_time
  )
values (#{userId,jdbcType=BIGINT}, #{loginName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{gender,jdbcType=INTEGER}, #{phone,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{employeeNum,jdbcType=VARCHAR}, #{isEmployment,jdbcType=INTEGER}, 
  #{entryDate,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 
  
  from user
  where login_name = #{loginName,jdbcType=VARCHAR}
  and password = #{password,jdbcType=VARCHAR}

select count(user_id) 
from user
where name like #{name,jdbcType=VARCHAR}

select 
 
from user
where name like #{name,jdbcType=VARCHAR}
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
